import unittest

from Bio.SeqUtils.ProtParam import ProteinAnalysis
from Bio import SeqIO
from Bio import Seq
from backend.utility.protein import Protein
from two_de_simulation import Simulation_2de
class Test_2d_Simulation(unittest.TestCase):
    def test_parse_fasta_content(self):
        '''Given an amino acid sequence parse_fasta_content should parse it into a dict with relent information stored'''
        expected = [{'header': 
                     'gi|2765658|emb|Z78533.1|CIZ78533 C.irapeanum 5.8S rRNA gene and ITS1 and ITS2 DNA', 
                     'sequence': 
                     '', 
                     'name': 'gi|2765658|emb|Z78533.1|CIZ78533 C.irapeanum 5.8S rRNA gene and ITS1 and ITS2 DNA', 
                     'organism': 'Unknown organism', 
                     'mw': 60302.28070000022, 
                     'pH': 5.086544227600096}]
        achual = Simulation_2de.parse_fasta_content('')
        self.assertEqual(expected,achual)

    def test_parse_fasta(self):
        '''Given a sequecne from parse_fasta_content should add addional data to the dict'''
        expected_keys = Simulation_2de.parse_fasta_content('')[0].keys()
        achual_keys = ['name', 'fullName', 'organism', 'uniprotId', 'mw', 'pH', 'color', 'sequence', 'x', 'y', 'currentpH', 'velocity', 'settled', 'ID', 'Link', 'display_name']
        self.assertEqual(expected_keys,achual_keys)
    if (__name__ == "__name__"):
        test_parse_fasta()